var topCalc = function(values, data, calcParams){
    var total = 0;
    var calc = 0;
    var count = 0;
    data.forEach(value => {
        total+=Number(value.numberValue);
        count++;
    });
    if (count>0) calc=(total/count).toFixed(2);
    return `${calc} (avg)`;
}

var legend = function (value) {
    if (value<100) return "<span style='color:#FF0000;'>"+value+"</span>";
    else return "<span style='color:#000000;'>"+value+"</span>"; 
}
